import React, { useEffect }from 'react';
import './Slider.css';
import M from 'materialize-css';
import img1 from './utils/images/img1.jpg';
import img2 from './utils/images/img2.jpg';
import img3 from './utils/images/img3.jpg';

export function Slider(){

  useEffect(()=>{
    let slider = document.querySelector('.slider');
    M.Slider.init(slider, {
      indicators: false,
      height: 500,
      transition: 100,
      interval: 6000
    });
  })


  return (
    <>
      <div className="slider column s12" id='myDiv'>
        <ul className="slides" >
          <li>
            <img src={img1}  alt="" className="responsive-img testImg"/>
            <div className="caption center-align">
              <h3>This is our big Tagline!</h3>
              <h5 className="light grey-text text-lighten-3">Here's our small slogan.</h5>
                <button className="btn" >Contact Us
                </button>
            </div>
          </li>
          <li>
            <img src={img2}  alt="" className="responsive-img"/>
            <div className="caption right-align">
              <h3>This is our big Tagline!</h3>
              <h5 className="light grey-text text-lighten-3">Here's our small slogan.</h5>
            </div>
          </li>
          <li>
            <img src={img3}  alt="" className="responsive-img"/>
            <div className="caption left-align">
              <h3>This is our big Tagline!</h3>
              <h5 className="light grey-text text-lighten-3">Here's our small slogan.</h5>
            </div>
          </li>
        </ul>
      </div>

    </>
  )
}
