import React , {useEffect} from 'react';
import './Text.scss';
import lottie from 'lottie-web';
import busyTeam from './utils/animation/business-team.json';


export function Text() {

  useEffect (() => {
      const anim = lottie.loadAnimation({
        container: document.querySelector('#leftBot'),
        animationData: busyTeam,
        renderer: 'svg',
        loop: false,
        autoplay: false,

    })
    let cavas = document.querySelector('#busyTeam');
    cavas.addEventListener('mouseenter', (e)=>{
        lottie.setDirection(1);
        lottie.play();
    })
    anim.onComplete= function() {
        lottie.stop();
    }
  }, []) 

  return (

      <div className="Parallax__background__animation__BT" id="leftBot">
          <div id='busyTeam' />      
      </div> 

  )
}
