import React, { useEffect } from 'react';
import './Intro.css';
import lottie from 'lottie-web';
import animationData from './utils/animation/test.json';

export function Intro() {

    useEffect(() => {
        lottie.loadAnimation({
            container: document.querySelector('#test-a'),
            animationData: animationData,
            renderer: 'svg',
            loop: false,
            autoplay: false
        })
        let cavas = document.querySelector('#test-a');
        cavas.addEventListener('mouseenter', ()=>{
            lottie.play();
        })
    },[lottie])

    return (
        <div className="animation" id="test">

            <div id='test-a' style={{width: 300, height:300}}/>      

        </div>
    );
}
