import React , {useEffect} from 'react';
import './Text.scss';
import lottie from 'lottie-web';
import programMan from './utils/animation/programming-man.json';
import gear from './utils/animation/47-gears.json';
import videoDesign from './utils/animation/video-design.json';
import dev from './utils/animation/web-development.json';
import codeDebug from './utils/animation/code-debugging.json';
import smiley from './utils/animation/smiley-stack.json';
import dog from './utils/animation/dog.json';

import worker from './utils/animation/worker-have-an-idea.json';



export function Text() {

  useEffect (() => {

      const anim = lottie.loadAnimation({
        container: document.querySelector('.Parallax__background__animation__Dev'),
        animationData: dev,
        renderer: 'svg',
        loop: false,
        autoplay: false,
        rendererSettings: {
          progressiveLoad: false
        }

    })
    const anin = lottie.loadAnimation({
      container: document.querySelector('.Parallax__background__animation__codeDebug'),
      animationData: codeDebug,
      renderer: 'svg',
      loop: false,
      autoplay: false,
      rendererSettings: {
        progressiveLoad: false
      }

  })
  let cava = document.querySelector('.Parallax__background__animation__codeDebug');
  cava.addEventListener('mouseenter', (e)=>{
      lottie.setDirection(1);
      // lottie.setSpeed(2);
      lottie.play();
  })
  anin.onComplete= function() {
      lottie.stop();
  }
    let cavas = document.querySelector('.Parallax__background__animation__Dev');
    cavas.addEventListener('mouseenter', (e)=>{
        lottie.setDirection(1);
        // lottie.setSpeed(2);
        lottie.play();
    })
    anim.onComplete= function() {
        lottie.stop();
    }
  }, []) 

  return (
    <div>
          <div className="Parallax__background__animation__Dev"  > </div>  

          <div className="Parallax__background__animation__codeDebug"  > </div>   
    </div>




  )
}
