import React, { useEffect } from 'react';
import './Story.scss';
import img1  from './utils/images/TealGear.png';
import img2  from './utils/images/MustardGear.png';
import img3  from './utils/images/CoralGear.png';


export function Story() {



    useEffect(() => {
        const images = document.querySelectorAll('.ig');
        var index = 0;
        const slideUpdate = () => {
            var i;
            for(i = 0; i < images.length; i++) {
                images[i].style.display="none";
            }
            index++;
            images[index-1].style.display="block";
            if(index > images.length) {
                index = 1;
            }
            setInterval(slideUpdate, 2000);
        }
        slideUpdate();
    })

    return (
        <div className="story" id="Story">
            <div className="story__slider">
                <div className="story__slider__demo">
                    <div className="story__slider__demo__left">
                        <img src ={img1} alt='' className="ig" ></img>
                        <img src ={img2} alt='' className="ig" ></img>
                        <img src ={img3} alt='' className="ig" ></img>
                    </div>

                </div>
                <div className="story__slider__title">

                </div>
            </div>
            <div className="story__talker">

            </div>
        </div>
    )
}
