import React, { useEffect } from 'react';
import './Footer.scss';
import lottie from 'lottie-web';
import deadpool from './utils/animation/walking-pumpkin.json';

export function Footer() {

    useEffect(()=> {
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump1'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump2'),
            animationData: deadpool,
