            container: document.querySelector('.walkingPump8'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump9'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
