        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump10'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump11'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump12'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
    })
    return (
        <div className="footer" id="Footer">
            <div className="footer__container">
                <div className='footer__container__title'>
                    <h1>Welcome to Rift</h1>
                </div>
                <div className='footer__container__footer'>
                    <ul>
                        <li><a className="grey-text text-lighten-3" href="#!"><span>TERMS OF SERVICE</span></a></li>
