import React, { useEffect } from 'react';
import './Story.scss';
import img3  from './utils/images/laptops-right.jpg';
import top1  from './utils/images/top1.jpg';
import bot1  from './utils/images/bot1.jpg';
import left1  from './utils/images/left1.jpeg';
import right1 from './utils/images/right1.jpg';
import top2  from './utils/images/top2.jpg';
import bot2  from './utils/images/bot2.jpg';
import left2  from './utils/images/left2.jpeg';
import right2 from './utils/images/right2.jpg';


export function Story() {

    useEffect(() => {
        var index = 1;

        var timer =null;

        const imagesA = document.getElementsByClassName("ig");
        const imagesB = document.getElementsByClassName("bg");
        const imagesC = document.getElementsByClassName("cg");
        const imagesD = document.getElementsByClassName("dg");
        const titles = document.querySelectorAll(".title");

        slideUpdate(index);

        function manualSlide (n) {
            clearTimeout(timer);
            slideUpdate(index = n);
        }

        titles.forEach(title=> {
            title.addEventListener("click", () => {
                var key = title.getAttribute("data-key");
                manualSlide(key);
                title.setAttribute('id', 'titleActive');
            })
        })

        function slideUpdate (n)  {
            var i;
            if (n===undefined) {
                n = ++index;
            }
            if(index > titles.length) {
                index = 1;
            }
            for(i = 0; i < titles.length; i++) {
                imagesA[i].style.opacity="0";
                imagesA[i].style.zindex="0";
                imagesB[i].style.opacity="0";
                imagesB[i].style.zindex="0";
                imagesC[i].style.opacity="0";
                imagesC[i].style.zindex="0";
                imagesD[i].style.opacity="0";
                imagesD[i].style.zindex="0";
                titles[i].removeAttribute('id','titleActive');
            }

            imagesA[index-1].style.opacity="1";
            imagesA[index-1].style.zindex="1";
            imagesB[index-1].style.opacity="1";
            imagesB[index-1].style.zindex="1";
            imagesC[index-1].style.opacity="1";
            imagesC[index-1].style.zindex="1";
            imagesD[index-1].style.opacity="1";
            imagesD[index-1].style.zindex="1";
            titles[index-1].setAttribute('id','titleActive');

            timer = setTimeout(slideUpdate, 5000);
        }

    },[])

    return (
        <div className="story" id="Story">
            <div className="story__slider">
                <div className="story__slider__demo">
                    <div className="story__slider__demo__left">
                        <img src ={left1} alt='' className="ig" ></img>
                        <img src ={left2} alt='' className="ig" ></img>           
                        <img src ={img3} alt='' className="ig" ></img>
                    </div>
                    <div className="anotherbox">
                        <div className="story__slider__demo__top">
                            <img src ={top1} alt='' className="bg" ></img>
                            <img src ={top2} alt='' className="bg" ></img>           
                            <img src ={img3} alt='' className="bg" ></img>
                        </div>
                        <div className="story__slider__demo__bottom">
                            <img src ={bot1} alt='' className="cg" ></img>
                            <img src ={bot2} alt='' className="cg" ></img>           
                            <img src ={img3} alt='' className="cg" ></img>
                        </div>
                    </div>
                    <div className="story__slider__demo__right">
                        <img src ={right1} alt='' className="dg" ></img>
                        <img src ={right2} alt='' className="dg" ></img>           
                        <img src ={img3} alt='' className="dg" ></img>
                    </div>

                </div>
                <div className="story__slider__title">
                    <h4 className="title A" data-key="1" >Industry leaders</h4>
                    <h4 className="title B" data-key="2" >Global community</h4>
                    <h4 className="title C" data-key="3" >Content Generator</h4>
                </div>
            </div>
            <div className="story__talker">

            </div>
        </div>
    )
}
