import React , {useEffect} from 'react';
import './Text.scss';
import lottie from 'lottie-web';
import programMan from './utils/animation/programming-man.json';
import gear from './utils/animation/47-gears.json';
import videoDesign from './utils/animation/video-design.json';
import dev from './utils/animation/web-development.json';
import codeDebug from './utils/animation/code-debugging.json';
import smiley from './utils/animation/smiley-stack.json';
import dog from './utils/animation/dog.json';





import puzzle from './utils/animation/puzzle-animation.json';
import recolorPlant from './utils/animation/recolor-plant.json';
import TB from './utils/animation/team-of-businessman.json';
import worker from './utils/animation/worker-have-an-idea.json';
import bookGirl from './utils/animation/bookgirl.json';
import blueCycle from './utils/animation/blue-cycle.json';



export function Text() {

  useEffect (() => {
      const anim = lottie.loadAnimation({
        container: document.querySelector('.Parallax__background__animation__BT'),
        animationData: puzzle,
        renderer: 'svg',
        loop: false,
        autoplay: false,
        rendererSettings: {
          progressiveLoad: false
        }

    })
    let cavas = document.querySelector('.Parallax__background__animation__BT');
    cavas.addEventListener('mouseenter', (e)=>{
        lottie.setDirection(1);
        // lottie.setSpeed(2);
        lottie.play();
    })
    anim.onComplete= function() {
        lottie.stop();
    }
  }, []) 

  return (
      // <div className="Parallax__background__animation__BT" id="leftBot">
          <div className="Parallax__background__animation__BT"  >   </div>  
      // </div> 
  )
}
