import React, { useEffect } from 'react';
import './Intro.css';
import lottie from 'lottie-web';
import busyTeam from './utils/animation/business-team.json';
import rightCloud  from './utils/images/rightCloud.png';
import leftCloud  from './utils/images/leftCloud.png';

export function Intro() {

    useEffect(() => {
        const anim = lottie.loadAnimation({
            container: document.querySelector('#test-a'),
            animationData: busyTeam,
            renderer: 'svg',
            loop: false,
            autoplay: false
        })
        let cavas = document.querySelector('#test-a');
        cavas.addEventListener('mouseenter', (e)=>{
            lottie.setDirection(1);
            lottie.play();
        })
        anim.onComplete= function() {
            lottie.stop();
        }
        window.addEventListener('scroll', ()=>{
            var scrolltotop = document.scrollingElement.scrollTop;
            var target = document.getElementById("test");
            var xvalue = "center";
            var factor = 0.3;
            var yvalue = scrolltotop * factor;
            target.style.backgroundPosition = xvalue + " " + yvalue + "px";
        })
    },[])

    return (
        <>
        <div className='intro' >
            <div className='cloudLayout' >
                <div className='introPage' >
                    <div className='background' id='body'>
                         <div id='test-a' style={{width: 300, height:300}}/>      
                        <img src ={rightCloud} alt=''  className='paraLC'></img>
                    </div>
                    {/* <div className="animation" id="test">

                    </div> */}
                    {/* <div className='rightTop'>
                        <img src ={rightCloud} alt='' className='rightTC' ></img>
                        <div className="animation" id="test">
                            <div id='test-a' style={{width: 300, height:300}}/>      
                        </div>
                    </div> */}

                    {/* <div className='leftTop'>
                        <img src ={leftCloud} alt='' className='leftTC' ></img>
                    </div>
                    <div className='leftBottom'>
                        <img src ={rightCloud} alt='' className='paraLC' ></img> 
                    </div>
                    <div className='leftBottom'>
                        <img src ={rightCloud} alt='' className='paraRC' ></img>
                    </div> */}
                    <div className='foreground'></div>
                </div>
                <div className='parallax'>
                      {/* <img src ={leftCloud} alt='' className='leftBLC' ></img>
                      <img src ={leftCloud} alt='' className='leftBRC' ></img>
                      <img src ={rightCloud} alt='' className='rightBRC' ></img>
                      <img src ={rightCloud} alt='' className='rightBLC' ></img>
                      <img src ={rightCloud} alt='' className='rightBMC' ></img> */}

                </div>

            </div>

        </div>
        </>


    );
}
