import React , { useEffect } from 'react';
import './Test.scss';
import people  from './utils/images/people.png';
export function Test() {

    useEffect(()=> {
        const carousel = document.querySelector('.carousel');
        const slider = document.querySelector('.peopleSlide');

        const next = document.querySelector('.next');
        const prev = document.querySelector('.prev');
        let direction;

        next.addEventListener('click', function() {
          direction = -1;
          carousel.style.justifyContent = 'flex-start';
          slider.style.transform = 'translate(-20%)';  
        });

        prev.addEventListener('click', function() {
          if (direction === -1) {
            direction = 1;
            slider.appendChild(slider.firstElementChild);
          }
          carousel.style.justifyContent = 'flex-end';    
          slider.style.transform = 'translate(20%)';  

        });

        slider.addEventListener('transitionend', function() {
          // get the last element and append it to the front

          if (direction === 1) {
            slider.prepend(slider.lastElementChild);
          } else {
            slider.appendChild(slider.firstElementChild);
          }

          slider.style.transition = 'none';
          slider.style.transform = 'translate(0)';
          setTimeout(() => {
            slider.style.transition = 'all 0.5s';
          })
        }, false);
    })

    return (
        <div class="container">
  <div class="carousel">
  <div className="peopleSlide">
                                    <img src ={people} alt='' className="people" ></img>
                                    <img src ={people} alt='' className="people" ></img>
                                    <img src ={people} alt='' className="people" ></img>
                                    <img src ={people} alt='' className="people" ></img>

                                </div>
    <div class="controls">
      <button class="next"><i class="material-icons">
        keyboard_arrow_right
        </i></button>
      <button class="prev"><i class="material-icons">
        keyboard_arrow_left
        </i></button>
    </div>
  </div>
</div>
    )
}
