            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump13'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump14'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump15'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump16'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
        lottie.loadAnimation({
            container: document.querySelector('.walkingPump17'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false
            }
        })
