import React, { useEffect } from 'react';
import './Intro.css';
import Lottie from 'react-lottie';
import animationData from './utils/animation/test';

export function Intro() {

    useEffect(() => {
        let animationObject = document.querySelector('.test');
        let loadingAni = animationObject.loadAnimation();
        animationObject.addEventListener('mouseenter', (e)=>{
            loadingAni.play();
        })
    },[])

    const defaultOptions = {
        loop: false,
        autoplay: false,
        animationData: animationData,
        rendererSettings: {
            preserveAspectRatio: 'xMidYMid slice'
          }
    }



    return (
        <div className="animation">
            <Lottie options={defaultOptions}
                    height={300}
                    width= {300}
                    id="test"
                    />

        </div>
    );
}
