import React, { useEffect } from 'react';
import './Footer.scss';
import lottie from 'lottie-web';
import deadpool from './utils/animation/deadpool.json';
import { Background } from 'react-parallax';

export function Footer() {

    useEffect(()=> {
        lottie.loadAnimation({
            container: document.querySelector('.animationArea'),
            animationData: deadpool,
            renderer: 'svg',
            loop: true,
            autoplay: true,
            rendererSettings: {
            progressiveLoad: false,
            background: transparent
            }
        })
    })
    return (
        <div className="footer" id="Footer">
            <div className="footer__container">
                <div className='footer__container__title'>
                    <h1>Welcome to Rift</h1>
                </div>
                <div className='footer__container__footer'>
                    <ul>
                        <li><a className="grey-text text-lighten-3" href="#!"><span>TERMS OF SERVICE</span></a></li>
                        <li><a className="grey-text text-lighten-3" href="#!"><span>PRIVACY NOTICE</span></a></li>
                        <li><a className="grey-text text-lighten-3" href="#!"><span>TECHNICAL SUPPORT</span></a></li>
                        <li><a className="grey-text text-lighten-3" href="#!"><span>LEADERSHIP</span></a></li>
                    </ul>
                </div>
                <div className="animationArea">

                </div>
            </div>
            <div className="footer-copyright">
                    Â© 2020 Guohao Ouyang. All Rights Reserved
            </div>
        </div>
    )
}
