import React, { useEffect } from 'react';
import './Story.scss';
import img1  from './utils/images/cups.jpg';
import img2  from './utils/images/laptops-left.jpg';
import img3  from './utils/images/laptops-right.jpg';


export function Story() {



    useEffect(() => {

        var index = 0;
        slideUpdate();
        function slideUpdate ()  {
            var i;
            const images = document.getElementsByClassName("ig");
            for(i = 0; i < images.length; i++) {
                images[i].style.opacity="0";
                images[i].style.zIndex="0";
            }
            index++;
            if(index > images.length) {
                index = 1;
            }
            images[index-1].style.opacity="1";
            images[i].style.zIndex="1";

            // setTimeout(slideUpdate, 5000);
        }

    })

    return (
        <div className="story" id="Story">
            <div className="story__slider">
                <div className="story__slider__demo">
                    <div className="story__slider__demo__left">
                        <img src ={img1} alt='' className="ig" ></img>
                        <img src ={img2} alt='' className="ig" ></img>           
                        <img src ={img3} alt='' className="ig" ></img>
                    </div>

                </div>
                <div className="story__slider__title">

                </div>
            </div>
            <div className="story__talker">

            </div>
        </div>
    )
}
